import convertToRupiah from "../utils/ToRupiah";

export const Card = ({ product, fromProduct, addProductToCart, url }) => {
	const { tittle, image, price } = product;
	// console.log(product);
	return (
		<div className="mb-3 card">
			<div className="py-2 px-2">
				<img
					src={url + image}
					alt={tittle}
					style={{
						height: "140px",
						objectFit: "cover",
						width: "100%",
					}}
				/>
			</div>
			<div className="card-body ">
				<div
					style={{
						height: "38px",
					}}
				>
					<h5
						style={{
							fontSize: "15px",
							fontWeight: "600",
						}}
					>
						{tittle}
					</h5>
				</div>
				<small className="text-danger">{convertToRupiah(price)}</small>
				<div className=" d-flex justify-content-center align-items-center flex-column">
					<button
						onClick={() => addProductToCart(product)}
						className="mt-2 btn btn-yellow btn-block"
					>
						Add To Cart
					</button>
					<button
						onClick={() => addProductToCart(product)}
						className="mt-2 btn btn-yellow btn-block"
					>
						Add To Cart
					</button>
				</div>
			</div>
		</div>
	);
};

export default Card;
