import { useHistory } from "react-router";
import { Container, Col, Row } from "react-bootstrap";

const CardPopular = (props) => {
	const { name, logo, id } = props.data;
	const history = useHistory();
	return (
		<>
			<Col xs={12} md={6} lg={3} className="mb-3">
				<Container>
					<Row
						className="bg-white py-3 avenir"
						style={{ borderRadius: 5, cursor: "pointer", minHeight: "140px" }}
						onClick={() => {
							history.push(`/restaurant/${id}`);
						}}
						whileHover={{
							scale: 1.1,
						}}
						transition={{ type: "spring", stiffness: 600 }}
					>
						<Col xs={3} className="text-center">
							<img
								src={logo}
								style={{ width: "65px", height: "65px", objectFit: "cover" }}
								alt={name}
							/>
						</Col>
						<Col xs={9} className="my-auto text-center">
							<h3 className=" my-0 font-weight-bold">{name}</h3>
						</Col>
					</Row>
				</Container>
			</Col>
		</>
	);
};

export default CardPopular;
