import { createContext, useReducer } from "react";

export const AuthContext = createContext();

const initialState = {
	modalLogin: false,
	modalRegister: false,
	email: null,
	user: {},
	isLogin: false,
	error: null,
	errormail: null,
	isLogin: null,
};

export const reducer = (state, { type, payload }) => {
	// console.log("dari context Auth :", payload);
	switch (type) {
		case "MODAL_LOGIN_OPEN":
			return {
				...state,
				modalLogin: true,
			};
		case "MODAL_LOGIN_CLOSE":
			return {
				...state,
				modalLogin: false,
			};
		case "MODAL_REGISTER_OPEN":
			return {
				...state,
				modalRegister: true,
			};
		case "MODAL_REGISTER_CLOSE":
			return {
				...state,
				modalRegister: false,
			};
		case "LOGOUT":
			console.log("logout");
			return {
				...state,
				user: {},
			};
		case "LOGIN":
			// console.log("login", payload);

			const checkUser = state.users.find((u) => u.email === payload.email);
			if (checkUser) {
				state.user = checkUser;

				return {
					...state,
					error: null,
				};
			} else {
			}

			return {
				...state,
				error: "email tidak di temukan",
			};
		case "REGISTER":
			// console.log("REgister", payload);
			const checkEmail = state.users.find((u) => u.email === payload.email);
			if (checkEmail) {
				return {
					...state,
					modalRegister: true,
					errormail: "email sudah terdaftar",
				};
			}
			return {
				...state,
				errormail: null,
				users: [
					...state.users,
					{
						...payload,
					},
				],
			};
		default:
			return state;
	}
};

export const AuthContextProvaider = ({ children }) => {
	const [state, dispatch] = useReducer(reducer, initialState);
	return (
		<AuthContext.Provider value={[state, dispatch]}>
			{children}
		</AuthContext.Provider>
	);
};
