import { useState } from "react";
import Popular from "../../compnents/card/CardPopular";
import { API } from "../../config/api";
import { useQuery, useMutation } from "react-query";

const CrudUser = () => {
	const { data: UsersData, loading, error, refetch } = useQuery(
		"userCache",
		async () => {
			const response = await API.get("/users");
			return response;
		}
	);

	return (
		<div>
			<div className="mt-2 mb-3">
				<form
					onSubmit={(e) => {
						e.preventDefault();
						if (idForUpdate) {
							updateTodo();
						} else {
							handleSubmit();
						}
					}}
				>
					<h3 className="text-center">Form Add User</h3>
					{addUser.error?.response?.data && (
						<div class="alert alert-danger" role="alert">
							{addUser.error?.response?.data?.message}
						</div>
					)}
					<div className="form-group">
						<label>Email</label>
						<input
							value={email}
							onChange={(e) => onChange(e)}
							name="email"
							type="text"
							className="form-control"
						/>
					</div>
					<div className="form-group">
						<label>Password</label>
						<input
							value={password}
							onChange={(e) => onChange(e)}
							name="password"
							type="text"
							className="form-control"
						/>
					</div>
					<div className="form-group">
						<label>Name</label>
						<input
							value={name}
							onChange={(e) => onChange(e)}
							name="name"
							type="text"
							className="form-control"
						/>
					</div>
					<div className="form-group">
						<label>Phone</label>
						<input
							value={phone}
							onChange={(e) => onChange(e)}
							name="phone"
							type="text"
							className="form-control"
						/>
					</div>

					<div className="form-group">
						<button
							className="btn btn-primary btn-block"
							disabled={!email || !password || !name || !phone ? true : false}
						>
							{idForUpdate ? "Update User" : "Submit User"}
						</button>
					</div>
				</form>
			</div>
			<div className="mt-3 row">
				<table className="table table-striped table-hovered">
					<thead>
						<tr>
							<th>No</th>
							<th>Email</th>
							<th>Name</th>
							<th>Phone</th>
							<th>Action</th>
						</tr>
					</thead>
					<tbody>
						{loading ? (
							<tr>
								<td colSpan="4">Loading Data</td>
							</tr>
						) : (
							UsersData?.data?.data?.users?.map((user, index) => (
								<TableRowUser
									user={user}
									index={index}
									key={user.id}
									getTodoById={getTodoById}
									deleteUserById={deleteUserById}
								/>
							))
						)}
					</tbody>
				</table>
			</div>
		</div>
	);
};

export default CrudUser;
