import map from "../../compnents/assets/img/map.svg";
import clip from "../../compnents/assets/img/paperclip.svg";
import Maps from "../../compnents/Maps";
import { useState } from "react";
import { API } from "../../config/api";
import { useQuery, useMutation } from "react-query";
// import { Button, Form, Modal } from "react-bootstrap";
import { Container, Col, Row, Form, Button, Modal } from "react-bootstrap";
const CreateRestaurant = () => {
	const [form, setForm] = useState({
		name: "",
		locationName: "",
		image: null,
	});

	const { name, image, locationName } = form;

	const {
		data: productData,
		error: productError,
		loading: productLoading,
		refetch: productRefetch,
	} = useQuery("productsCache", async () => {
		return API.get("/products");
	});

	const addProduct = useMutation(async () => {
		const body = new FormData();

		body.append("name", name);
		body.append("image", image);

		const config = {
			headers: {
				"Content-Type": "multipart/form-data",
			},
		};

		await API.post("/product", body, config);
		productRefetch();
		setForm({
			name: "",
			image: null,
		});
	});

	const onChange = (e) => {
		const tempForm = { ...form };
		tempForm[e.target.name] =
			e.target.type === "file" ? e.target.files[0] : e.target.value;
		setForm(tempForm);
	};
	return (
		<>
			<div className="container pt-3">
				<div className="row py-3 mt-3 mt-custom justify-content-center"></div>
			</div>
			<Container>
				<Row className="mb-4">
					<Col xs={12}>
						<h2 className="font-weight-bold">Create Restaurant</h2>
					</Col>
				</Row>
				<Row>
					<Col>
						<Form
							onSubmit={(e) => {
								e.preventDefault();
								addProduct.mutate();
							}}
						>
							<Row>
								<Col xs={12} lg={9}>
									<Form.Group>
										<Form.Control
											className="input-bg input-bg2"
											value={name}
											onChange={(e) => onChange(e)}
											name="name"
											type="text"
										/>
									</Form.Group>
								</Col>
								<Col xs={12} lg={3}>
									<Form.Group controlId="inputFile">
										<input
											type="file"
											// id="inputFile"
											// onChange={(e) => onChange(e)}
											// name="image"
											// type="file"
										/>
										<label for="file" className="custom ">
											Attach Image
											<img
												src={clip}
												alt="map"
												className=" imghover"
												style={{ float: "right", height: "20px" }}
											/>
										</label>
									</Form.Group>
								</Col>
							</Row>

							<Row>
								<Col xs={12} lg={9}>
									<Form.Group>
										<Form.Control className="input-bg input-bg2" />
									</Form.Group>
								</Col>
								<Col xs={12} lg={3}>
									<Form.Group>
										<Button variant="brown" className="btn-block">
											Select On Map
											<img src={map} alt="map" className="ml-2" />
										</Button>
									</Form.Group>
								</Col>
							</Row>

							<Row className="mt-2">
								<Col xs={12} lg={12} className="text-right">
									<Button
										variant="brown"
										className="w-25"
										type="submit"
										disabled={!name || !image ? true : false}
									>
										Save
									</Button>
								</Col>
							</Row>
						</Form>
					</Col>
				</Row>
			</Container>

			{/* <Maps show={modalShow} onHide={() => setModalShow(false)} /> */}
		</>
	);
};

export default CreateRestaurant;
