import map from "../../compnents/assets/img/map.svg";
import clip from "../../compnents/assets/img/paperclip.svg";
import Maps from "../../compnents/maps/MapModal";
import Pin from "../../compnents/maps/Pin";
import Address from "../../compnents/utils/Addres";
import { useState, useCallback, useEffect } from "react";
import { API } from "../../config/api";
import { useQuery, useMutation } from "react-query";
import ReactMapGL, { Marker, NavigationControl } from "react-map-gl";
import axios from "axios";

import {
	Container,
	Col,
	Row,
	Form,
	Button,
	Modal,
	Alert,
} from "react-bootstrap";
const CreateRestaurant = () => {
	const [modalShow, setModalShow] = useState(false);
	const [form, setForm] = useState({
		name: "",
		locationName: "Jl apa",
		imageFile: null,
		location: " 106.88203,-6.91506",
	});
	const { name, imageFile, locationName, location } = form;
	const addrestaurant = useMutation(async () => {
		const body = new FormData();

		body.append("name", name);
		body.append("location", location);
		body.append("imageFile", imageFile);

		const config = {
			headers: {
				"Content-Type": "multipart/form-data",
			},
		};

		await API.post("/restaurant", body, config);

		router.push("/");
	});
	const onChange = (e) => {
		const tempForm = { ...form };
		tempForm[e.target.name] =
			e.target.type === "file" ? e.target.files[0] : e.target.value;
		setForm(tempForm);
	};

	// marker
	const token =
		"pk.eyJ1IjoiYXJpc2FyZXMyMiIsImEiOiJja21hNHN4bncxbzN1Mm5wcnZqZmhmd245In0.mo6lAUdwinLZchtCADFthw";

	// const {
	// 	data: addressData,
	// 	error: addressError,
	// 	loading: addressLoading,
	// 	refetch: addressRefetch,
	// } = useQuery("addresssCache", async () => {
	// 	const data = await axios.get(
	// 		// `https://api.mapbox.com/geocoding/v5/mapbox.places/106.89258,-6.90629.json?access_token=${token}&types=poi&country=id&localregion=ID-JB`
	// 		`https://api.mapbox.com/geocoding/v5/mapbox.places/${location}.json?access_token=${token}&types=poi&country=id&localregion=ID-JB`
	// 	);
	// 	console.log("data", data.data);

	// 	setForm({
	// 		...form,
	// 		locationName: data?.data?.features[0]?.place_name,
	// 	});
	// });
	const [place, setPlace] = useState("");
	const [address, setAddress] = useState("");

	const [marker, setMarker] = useState({
		longitude: 106.91905,
		latitude: -6.9123,
	});
	const [events, logEvents] = useState({});
	const onMarkerDragStart = useCallback((event) => {
		logEvents((_events) => ({ ..._events, onDragStart: event.lngLat }));
	}, []);

	const onMarkerDrag = useCallback((event) => {
		logEvents((_events) => ({ ..._events, onDrag: event.lngLat }));
	}, []);

	const onMarkerDragEnd = useCallback((event) => {
		logEvents((_events) => ({ ..._events, onDragEnd: event.lngLat }));
		setMarker({
			longitude: event.lngLat[0],
			latitude: event.lngLat[1],
		});
		setForm({
			// ...form,
			location: event.lngLat[0] + "," + event.lngLat[1],
		});
	}, []);
	const fetchLocation = async () => {
		const token =
			"pk.eyJ1IjoiaWxoYW0yNSIsImEiOiJja20yczc0dm0zOWczMndwMzVmdmJ1bjI4In0.1l2Zgxjy5R0iW2SlySO_fQ";
		const apiUrl = `https://api.mapbox.com/geocoding/v5/mapbox.places/${location}.json?limit=1&access_token=${token}`;

		const api = await fetch(apiUrl);
		const response = await api.json();

		setPlace(response?.features[0]?.text);
		setAddress(response?.features[0]?.place_name);
	};

	// console.log("asas", onMarkerDragEnd)
	useEffect(() => {
		fetchLocation();
	}, [location]);
	const lokasi = Address(location);
	console.log("lokai", lokasi);
	return (
		<>
			<div className="container pt-3">
				<div className="row py-3 mt-3 mt-custom justify-content-center"></div>
			</div>
			<Container>
				<Row className="mb-4">
					<Col xs={12}>
						<h2 className="font-weight-bold">Create Restaurant</h2>
					</Col>
				</Row>
				<Row className="mb-4">
					<Col xs={12}>
						{addrestaurant.error?.response?.data?.message && (
							<Alert variant="danger">
								{addrestaurant.error?.response?.data?.message}
							</Alert>
						)}
					</Col>
				</Row>
				<Row>
					<Col>
						<Form
							onSubmit={(e) => {
								e.preventDefault();
								addrestaurant.mutate();
							}}
						>
							<Row>
								<Col xs={12} lg={9}>
									<Form.Group>
										<Form.Control
											className="input-bg input-bg2"
											value={name}
											onChange={(e) => onChange(e)}
											name="name"
											type="text"
											placeholder="Restaurant Name"
											required
										/>
									</Form.Group>
								</Col>
								<Col xs={12} lg={3}>
									<Form.Group>
										<input
											id="file"
											onChange={(e) => onChange(e)}
											name="imageFile"
											type="file"
										/>
										<label for="file" className="custom ">
											{imageFile?.name ? imageFile?.name : "Attach Image"}
											<img
												src={clip}
												alt="map"
