import { API } from "../../config/api";
import { useQuery } from "react-query";

//useQuery = method GET
//useMutation = method POST, PUT, PATCH & DELETE

const Multer = () => {
	const {
		data: productData,
		error: productError,
		loading: productLoading,
		refetch: productRefetch,
	} = useQuery("productsCache", async () => {
		return API.get("/products");
	});

	return (
		<div>
			<div className="mt-2 mb-3">
				<form
					onSubmit={(e) => {
						e.preventDefault();
						addProduct.mutate();
					}}
				>
					<div className="form-group">
						<label>Product Name</label>
						<input
							value={name}
							onChange={(e) => onChange(e)}
							name="name"
							type="text"
							className="form-control"
						/>
					</div>
					<div className="form-group">
						<label>Image</label>
						<input
							onChange={(e) => onChange(e)}
							name="image"
							type="file"
							className="form-control"
						/>
					</div>

					<div className="form-group">
						<button
							className="btn btn-primary btn-block"
							disabled={!name || !image ? true : false}
						>
							Submit Product
						</button>
					</div>
				</form>
			</div>
		</div>
	);
};

export default Multer;

const TableRowMulter = ({ product, index }) => {
	const { id, image, name, categories } = product;
	return (
		<tr>
			<td>{index + 1}</td>
			<td>{name}</td>
			<td>
				<ul>
					{categories.map((category) => (
						<li>{category.name}</li>
					))}
				</ul>
			</td>
			<td>
				<img
					src={image}
					alt={name}
					style={{
						width: "200px",
						height: "130px",
						objectFit: "cover",
					}}
				/>
			</td>

			<td>
				<button className="mr-2 btn btn-success btn-sm">Edit</button>
				<button className="btn btn-danger btn-sm">Delete</button>
			</td>
		</tr>
	);
};
