import { Link, useParams } from "react-router-dom";
import { API } from "../../config/api";
import { useQuery } from "react-query";

import { TextLoading } from "../../compnents/card/CardLoading";
import GetAddress from "../../compnents/utils/getAddress";

import "./assets/index.css";

const Restaurant = () => {
	const {
		data: myData,
		error: myError,
		loading: myLoading,
		refetch: myRefetch,
	} = useQuery("myCache", async () => {
		return await API.get(`/restaurant`);
	});
	let resData = null;
	let addres = null;
	if (myData?.data?.data?.restaurant?.name) {
		// resData.location = myData.data.data.restauran.location;
		// resData.image = myData.data.data.url + myData.data.data.restauran.image;
		// resData.name = myData.data.data.restauran.name;
		// resData.product = myData.data.data.restauran.products;
		// resData.transactios = myData.data.data.restauran.transactios;
		// resData = myData.data.data.restauran.location;
		resData = JSON.stringify(myData.data.data.restaurant);
	}
	const data = JSON.parse(resData);

	addres = GetAddress(data.location);
	// console.log("saa", addres);

	return (
		<>
			<div className=" mt-5 pb-3">
				<div className="container pt-3"></div>
			</div>
			<div className="container">
				{myData?.data?.data?.restaurant?.image ? (
					<>
						<div className="row">
							<div className="col-md-3">
								<div className="profile-sidebar">
									<div className="d-flex justify-content-center px-2">
										<img
											src={
												myData.data.data.url + myData.data.data.restaurant.image
											}
											className="img-responsive"
											alt=""
											style={{
												height: "200px",
												objectFit: "cover",
												width: "100%",
											}}
										/>
									</div>

									<div className="text-center mt-3">
										{myData.data.data.restaurant.name}
									</div>
									<div className="text-center mb-3 px-3">
										{addres}
										<small>{myData.data.data.restaurant.location}</small>
									</div>

									<div className="portlet light bordered">
										<div className="row list-separated profile-stat">
											<div className="col-md-6 col-sm-12">
												<div className="uppercase profile-stat-title">
													{myData.data.data.restaurant.transactions.length}
												</div>
												<div className="uppercase profile-stat-text">
													{" "}
													Transactions{" "}
												</div>
											</div>

											<div className="col-md-6 col-sm-12">
												<div className="uppercase profile-stat-title">
													{" "}
													{myData.data.data.restaurant.products.length}{" "}
												</div>
												<div className="uppercase profile-stat-text">
													{" "}
													Product{" "}
												</div>
											</div>
										</div>

										<div>
											<div className="profile-userbuttons">
												<Link type="button" className="btn btn-brown btn-sm">
													Edit Profile
												</Link>
											</div>
										</div>
									</div>
								</div>
							</div>
							<div className="col-md-9">
								<div className="profile-content">asasasa</div>
							</div>
						</div>
					</>
				) : (
					<>loading</>
				)}
			</div>
		</>
	);
};

export default Restaurant;
