import { API } from "../../config/api";
import { useQuery } from "react-query";

//useQuery = method GET
//useMutation = method POST, PUT, PATCH & DELETE

const Multer = () => {
	const {
		data: productData,
		error: productError,
		loading: productLoading,
		refetch: productRefetch,
	} = useQuery("productsCache", async () => {
		return API.get("/products");
	});

	return (
		<div>
			<div className="mt-3 row">
				<table className="table table-striped table-hovered">
					<thead>
						<tr>
							<th>No</th>
							<th>Name</th>
							<th>Image</th>
							<th>Action</th>
						</tr>
					</thead>
					<tbody>
						{productLoading ? (
							<tr>
								<td colSpan="4">Loading Data</td>
							</tr>
						) : (
							productData?.data?.data?.products?.map((product, index) => (
								<TableRowMulter
									product={product}
									index={index}
									key={product.id}
								/>
							))
						)}
					</tbody>
				</table>
			</div>
		</div>
	);
};

export default Multer;

const TableRowMulter = ({ product, index }) => {
	const { id, image, name, categories } = product;
	return (
		<tr>
			<td>{index + 1}</td>
			<td>{name}</td>
			<td>
				<ul>
					{categories.map((category) => (
						<li>{category.name}</li>
					))}
				</ul>
			</td>
			<td>
				<img
					src={image}
					alt={name}
					style={{
						width: "200px",
						height: "130px",
						objectFit: "cover",
					}}
				/>
			</td>

			<td>
				<button className="mr-2 btn btn-success btn-sm">Edit</button>
				<button className="btn btn-danger btn-sm">Delete</button>
			</td>
		</tr>
	);
};
