import map from "../../compnents/assets/img/map.svg";
import clip from "../../compnents/assets/img/paperclip.svg";
import Maps from "../../compnents/Maps";
import { useState } from "react";
import { API } from "../../config/api";
import { useQuery, useMutation } from "react-query";

const CreateRestaurant = () => {
	const [form, setForm] = useState({
		name: "",
		locationName: "",
		image: null,
	});

	const { name, image, locationName } = form;

	const {
		data: productData,
		error: productError,
		loading: productLoading,
		refetch: productRefetch,
	} = useQuery("productsCache", async () => {
		return API.get("/products");
	});

	const addProduct = useMutation(async () => {
		const body = new FormData();

		body.append("name", name);
		body.append("image", image);

		const config = {
			headers: {
				"Content-Type": "multipart/form-data",
			},
		};

		await API.post("/product", body, config);
		productRefetch();
		setForm({
			name: "",
			image: null,
		});
	});

	const onChange = (e) => {
		const tempForm = { ...form };
		tempForm[e.target.name] =
			e.target.type === "file" ? e.target.files[0] : e.target.value;
		setForm(tempForm);
	};
	return (
		<>
			<div className="container pt-3">
				<div className="row py-3 mt-3 mt-custom justify-content-center">
					<form
						onSubmit={(e) => {
							e.preventDefault();
							addProduct.mutate();
						}}
					>
						<div className="my-2 col-10 ">
							<div className="mb-4">
								<h2>Create Restaurant</h2>
							</div>
							<div className="input-group mb-3">
								<div className="col px-0">
									<input
										className="form-control"
										placeholder="Full Name"
										value={name}
										onChange={(e) => onChange(e)}
										name="name"
										type="text"
									/>
								</div>
								<div className="col-3 pr-0">
									<input
										id="file"
										onChange={(e) => onChange(e)}
										name="imageFile"
										type="file"
									/>
									<label for="file" className="custom ">
										Attach Image
										<img
											src={clip}
											alt="map"
											className=" imghover"
											style={{ float: "right", height: "20px" }}
										/>
									</label>
								</div>
							</div>
							<div className="input-group mb-3">
								<div className="col px-0">
									<input
										type="text"
										className="form-control"
										placeholder="Location"
									/>
								</div>
								<div className="col-3 pr-0">
									<button className="btn btn-brown btn-block" type="submit">
										Select On Map
										<img src={map} alt="map" className="ml-2" />
									</button>
								</div>
							</div>
						</div>
						<div className="my-2 col-10 row px-0 ">
							<div className="col"></div>
							<div className="col-4">
								<button
									className="btn btn-brown btn-block"
									type="submit"
									// onClick={() => setModalShow(true)}
								>
									Save
								</button>
							</div>
						</div>
					</form>
				</div>
			</div>
			{/* <Maps show={modalShow} onHide={() => setModalShow(false)} /> */}
		</>
	);
};

export default CreateRestaurant;
