import { useContext, useEffect } from "react";
import { KeranjangContext } from "../../contexts/keranjangContext";
import { useParams } from "react-router-dom";
import { API } from "../../config/api";
import { useQuery } from "react-query";

import { Card } from "../../compnents/Card";
import Heromenu from "../../compnents/hero/Heromenu";
import { TextLoading } from "../../compnents/card/CardLoading";

import "./assets/index.css";

function Restaurant() {
	const [state, dispatch] = useContext(KeranjangContext);
	let { id } = useParams();
	const addProductToCart = (data) => {
		dispatch({
			type: "ADD_CART",
			payload: data,
		});
	};
	const {
		data: bookData,
		error: bookError,
		loading: bookLoading,
		refetch: bookRefetch,
	} = useQuery("booksCache", async () => {
		return API.get(`/products/${id}`);
	});

	return (
		<>
			<div className=" mt-5 pb-3">
				<div className="container pt-3"></div>
			</div>
			<div className="container">
				<div className="row">
					<div className="col-md-3">
						<div className="profile-sidebar">
							<div className="d-flex justify-content-center px-2">
								<img
									src="https://gravatar.com/avatar/31b64e4876d603ce78e04102c67d6144?s=80&d=https://codepen.io/assets/avatars/user-avatar-80x80-bdcd44a3bfb9a5fd01eb8b86f9e033fa1a9897c3a15b33adfc2649a002dab1b6.png"
									className="img-responsive"
									alt=""
									style={{
										height: "200px",
										objectFit: "cover",
										width: "100%",
									}}
								/>
							</div>

							<div className="text-center mt-3">
								Nama Restauran
								<small>lokasihjhjkhjkhkjhk</small>
							</div>

							<div className="portlet light bordered">
								<div className="row list-separated profile-stat">
									<div className="col-md-6 col-sm-12">
										<div className="uppercase profile-stat-title"> 37 </div>
										<div className="uppercase profile-stat-text">
											{" "}
											Transactions{" "}
										</div>
									</div>

									<div className="col-md-6 col-sm-12">
										<div className="uppercase profile-stat-title"> 61 </div>
										<div className="uppercase profile-stat-text"> Product </div>
									</div>
								</div>

								<div>
									<div className="profile-userbuttons">
										<button type="button" className="btn btn-brown btn-sm">
											Edit Profile
										</button>
									</div>
								</div>
							</div>
						</div>
					</div>
					<div className="col-md-9">
						<div className="profile-content">
							Some user related content goes here...
						</div>
					</div>
				</div>
			</div>
		</>
	);
}

export default Restaurant;
