import { useContext, useState } from "react";
import { Link } from "react-router-dom";

import { AuthContext } from "../contexts/authContext";

import { Button, Form, Modal, Alert } from "react-bootstrap";
import { API } from "../config/api";
import { useQuery, useMutation } from "react-query";

function Register() {
	const router = useHistory();
	const [state, dispatch] = useContext(AuthContext);
	const [form, setForm] = useState({
		email: "",
		fullName: "",
		password: "1",
		phone: "",
		gender: "",
	});

	const { email, password, fullName, phone, gender } = form;

	const addUser = useMutation(async () => {
		const config = {
			headers: {
				"Content-Type": "application/json",
			},
		};

		const body = JSON.stringify({
			email,
			password,
			fullName,
			phone,
			gender,
		});

		await API.post("/user", body, config);
	});

	// const handleSubmit = () => {
	// 	addUser.mutate();
	// };

	const handleClose = () => {
		dispatch({
			type: "MODAL_REGISTER_CLOSE",
		});
	};
	const handleOpenLogin = () => {
		handleClose();
		dispatch({
			type: "MODAL_LOGIN_OPEN",
		});
	};
	const handleOpenRegister = () => {
		dispatch({
			type: "MODAL_REGISTER_OPEN",
		});
	};
	const onChange = (e) => {
		setForm({
			...form,
			[e.target.name]: e.target.value,
		});
	};

	const registerUser = (e) => {
		e.preventDefault();
		addUser.mutate();
		handleClose();
		setForm({
			email: "",
			fullName: "",
			password: "1",
			phone: "",
			gender: "",
		});
		dispatch({
			type: "LOGIN_SUCCESS",
			payload: addUser.data.data.user,
		});
		setAuthToken(addUser.data.data.user.token);
		router.push("/");
	};
	// console.log("register", state);
	return (
		<Modal show={state.modalRegister} onHide={handleClose} size="sm" centered>
			<Modal.Body>
				<div className="form-title mb-3">
					<h4 className="text-yellow">Register</h4>
				</div>
				{addUser.error?.response?.data?.message && (
					<Alert variant="danger">
						{addUser.error?.response?.data?.message}
					</Alert>
				)}
				<div className="d-flex flex-column">
					<Form onSubmit={(e) => registerUser(e)}>
						<Form.Group controlId="formBasicEmail">
							<Form.Control
								type="email"
								className="form-control input-bg"
								placeholder="Email"
								value={form.email}
								name="email"
								required
								onChange={(e) => onChange(e)}
							/>
						</Form.Group>
						<Form.Group controlId="formBasicPassword">
							<Form.Control
								type="password"
								className="form-control input-bg"
								placeholder="Password"
								required
							/>
						</Form.Group>
						<Form.Group>
							<Form.Control
								type="text"
								className="form-control input-bg"
								placeholder="Full Name"
								name="name"
								value={form.name}
								onChange={(e) => onChange(e)}
								required
							/>
						</Form.Group>
						<Form.Group>
							<Form.Control
								type="text"
								className="form-control input-bg"
								placeholder="Gender"
								required
							/>
						</Form.Group>
						<Form.Group>
							<Form.Control
								type="text"
								className="form-control input-bg"
								placeholder="Phone"
								name="phone"
								value={form.phone}
								onChange={(e) => onChange(e)}
								required
							/>
						</Form.Group>
						{/* <Form.Group>
							<Form.Control
								as="select"
								className="input-bg"
								name="type"
								onChange={(e) => onChange(e)}
								value={form.type}
							>
								<option value="1">As User</option>
								<option value="2">As Partner</option>
							</Form.Control>
						</Form.Group> */}
						<Button
							type="submit"
							variant="brown"
							className="btn btn-block btn-round"
							// onClick={registerUser()}
						>
							Register
						</Button>
					</Form>
					<p className="text-danger">{state.errormail}</p>
					<div className="text-center text-muted delimiter mt-2 ">
						Already have an account ? klick {"\u00A0"}
						<Link
							to="#"
							onClick={handleOpenLogin}
							className="font-weight-bold text-muted"
						>
							here
						</Link>
						.
					</div>
				</div>
			</Modal.Body>
		</Modal>
	);
}

export default Register;
