import * as React from "react";
import { useState } from "react";
import { render } from "react-dom";
import MapGL, {
	Popup,
	NavigationControl,
	FullscreenControl,
	ScaleControl,
	GeolocateControl,
} from "react-map-gl";

// import ControlPanel from "./control-panel";
import Pins from "./pin2";

import CITIES from "./cities.json";
import CITIES2 from "./data.json";

const TOKEN =
	"pk.eyJ1IjoiYXJpc2FyZXMyMiIsImEiOiJja21hNHN4bncxbzN1Mm5wcnZqZmhmd245In0.mo6lAUdwinLZchtCADFthw";

const geolocateStyle = {
	top: 0,
	left: 0,
	padding: "10px",
};

const fullscreenControlStyle = {
	top: 36,
	left: 0,
	padding: "10px",
};

const navStyle = {
	top: 72,
	left: 0,
	padding: "10px",
};

const scaleControlStyle = {
	bottom: 36,
	left: 0,
	padding: "10px",
};

export default function App({ children }) {
	const [viewport, setViewport] = useState({
		longitude: 106.91905,
		latitude: -6.9123,
		zoom: 8,
		bearing: 0,
		pitch: 0,
	});
	const [popupInfo, setPopupInfo] = useState(null);

	function getSubTotal(data) {
		const [latitude, longitude] = data.split(",");
		return { latitude, longitude };
	}

	console.log("GHG", CITIES);
	return (
		<>
			<MapGL
				{...viewport}
				width="100%"
				height="100%"
				mapStyle="mapbox://styles/mapbox/dark-v9"
				onViewportChange={setViewport}
				mapboxApiAccessToken={TOKEN}
			>
				<Pins data={CITIES} onClick={setPopupInfo} />

				<GeolocateControl style={geolocateStyle} />
				<FullscreenControl style={fullscreenControlStyle} />
				<NavigationControl style={navStyle} />
				<ScaleControl style={scaleControlStyle} />
				{children}
			</MapGL>

			{/* <ControlPanel /> */}
		</>
	);
}
